export default function validateInfo(values) {
  let errors = {};

  if (!values.username.trim()) {
    errors.username = 'Username required';
  }
   //else if (!/^[A-Za-z]+/.test(values.name.trim())) {
     //errors.name = 'Enter a valid name';
  //}

  if (!values.NIU) {
    errors.NIU= 'NIU required';
  } else if (!/^d{2}\/\d{6}\/[a-zA-Z]{2}\/\d{5}/.test(values.NIU.trim())) {
    errors.NIU = 'Email address is invalid';
  }
  if (!values.choice) {
    errors.choice = 'Choice is required';
  } else if (values.password.length > 1 ) {
    errors.choice = 'Only Input from 1-3';
  }
  else if(values.password>3){
    errors.choice = 'Only Input from 1 or 2 or 3';
  }
  return errors;
}
